INSERT INTO `ila_community_networks_lang` (`id`, `community_network_id`, `title`, `content`, `language_id`) VALUES
(1, 1, 'ILACN Mission', 'To educate disadvantaged children in and around the communities in which ILA operates.', 1),
(2, 2, 'What we do', '<p>We direct our efforts towards projects:</p>\r\n									<ul class="white-box-blocks list-with-bullets">\r\n										<li>In which not only our teachers and staff but also our students and their parents can either donate their time, their skills or their money</li>\r\n										<li>To create sustainable programming focusing on educating disadvantaged children</li>\r\n										<li>To be able to show the progress over a committed period of time</li>\r\n									</ul>', 1),
(3, 3, 'How we do', '<p>We work closely to each non-profit organization to:</p>\r\n									<ul class="white-box-blocks list-with-bullets">\r\n										<li>Find out what they need.</li>\r\n										<li>Design educational programs based on their curriculum</li>\r\n										<li>Implement programs with the support of Volunteers from ILA.</li>\r\n									</ul>', 1),
(4, 4, 'Our projects', 'Each year, ILACN School Sponsorship Program sponsors the school fees for over 80 disadvantaged children from Elementary School to University, at our non-profit partners and in the community.&nbsp;<div><br></div><div>It is our most important program, which requires high commitment for the full year school fee to assure that our children can continue to complete their studying.&nbsp;</div><div><br></div><div>Let’s give us a hand by contacting Ms Betsye – ILACN at: betsye@ilavietnam.edu.vn.</div>', 1),
(5, 1, 'Sứ mệnh', 'Mang đến môi trường giáo dục cho những trẻ em kém may mắn tại và lân cận khu vực hoạt động của ILA.', 2),
(6, 2, 'Hoạt động', '<p>Chúng tôi luôn nỗ lực trong từng dự án nhằm mang đến những giá trị tốt đẹp cho cộng đồng.</p>\r\n									<ul class="white-box-blocks list-with-bullets">\r\n										<li>Bên cạnh sự tham gia của các giáo viên và nhân viên công ty, ILA Cộng đồng còn tạo cảm hứng và kêu gọi sự tham gia từ các quý phụ huynh và học sinh ILA cùng chung tay đóng góp thời gian, tâm huyết và vật chất cho các hoạt động của ILACN.</li>\r\n										<li>Hoạt động ILACN tập trung phát triển những chương trình giáo dục bền vữngcho các em kém may mắn.</li>\r\n										<li>Chúng tôi luôn nỗ lực để phát huy hiệu quả hoạt động của ILACN như đã cam kết nhằm mang đến những giá trị tốt đẹp cho cộng đồng.</li>\r\n									</ul>', 2),
(7, 3, 'Phương thức hoạt động', '<p>Chúng tôi làm việc trực tiếp với các tổ chức nhằm:</p>\r\n									<ul class="white-box-blocks list-with-bullets">\r\n										<li>Tìm hiểu nhu cầu của các Tổ chức</li>\r\n										<li>Thiết kế chương trình giáo dục phù hợp</li>\r\n										<li>Thực hiện các chương trình với sự giúp đỡ từ tình nguyện viên ILA</li>\r\n									</ul>', 2),
(8, 4, 'Các dự án', 'Hằng năm, chương trình Học bổng cho em đến trường tài trợ học phí cho hơn 80 em nhỏ có hoàn cảnh khó khăn từ cấp Tiểu học đến Đại học, tại các Mái ấm nơi ILACN đang hỗ trợ và ngoài cộng đồng.&nbsp;<div><br></div><div>Quỹ Học Bổng này là hoạt động trọng tâm của ILACN, đòi hỏi các cam kết lâu dài trong việc tài trợ học phí nguyên năm nhằm đảm bảo các em có thể hoàn thành việc học một cách liên tục.&nbsp;</div><div><br></div><div>Hãy đồng hành cùng ILACN bằng cách liên hệ Ms Betsye – Giám Đốc Bộ phận ILACN qua email: betsye@ilavietnam.edu.vn.</div>', 2);

