INSERT INTO `ila_news_lang` (`id`, `news_id`, `title`, `short_desc`, `content`, `language_id`) VALUES
(1, 1, 'Ra mắt cuốn sách "Hành trình kiến tạo thế hệ Việt Nam ưu việt" của GS John Vũ', 'Ngày 14/3, Trung tâm Anh ngữ ILA kết hợp với công ty Văn hóa sáng tạo Trí Việt (First News) tổ chức buổi họp báo nhân dịp ra mắt cuốn sách của giáo sư John Vũ với thông điệp “ Hành trình kiến tạo thế hệ Việt Nam ưu việt”. ', , 1),
(2, 2, 'ILA mở đường cho học viên đến Harvard', 'Học viên vẫn đang theo học tại ILA đến tháng 1.2022 có thể giành được học bổng toàn phần vào các trường đại học hàng đầu của thế giới: Harvard, Stanford, MIT… Giải thưởng lớn này chỉ dành cho 5 học viên đầu tiên của ILA có hồ sơ hợp lệ theo yêu cầu chương trình.', , 1),
(3, 3, 'Khởi động hành trình “Kiến tạo thế hệ Việt Nam ưu việt”', 'Mở đầu cho hành trình này là việc chính thức áp dụng phương pháp “Tư duy thế kỷ XXI” trong giảng dạy. Theo đó, trong năm 2017, tại 31 trung tâm của ILA, tất cả chương trình đào tạo, từ chương trình tiếng Anh trẻ em, tiếng Anh người lớn, tiếng Anh dành cho doanh nghiệp… đều được cải tiến.', , 1),
(4, 4, 'Tặng 10.000 sách của giáo sư Việt nổi tiếng thế giới cho học sinh', 'GS John Vũ - Nguyên Phong là nhà khoa học nổi tiếng ở Mỹ, đứng trong Top 10 người sáng tạo nhất thế giới (mà đứng đầu là Bill Gates và Steve Jobs). Giáo sư John Vũ cũng là một người rất quan tâm đến khoa học công nghệ, với trên 10.000 bài viết đã được đăng trên blog Science-Technology. Ông từng giữ vị trí Phó Chủ tịch phụ trách tất cả vấn đề về kỹ thuật của tập đoàn Boeing (CMM/CMMI). Ông cũng là sáng lập viên của SPIN Seatle và quản lý tổ chức này từ 1996 tới 2003.', , 1);
INSERT INTO `ila_news_lang` (`id`, `news_id`, `title`, `short_desc`, `content`, `language_id`) VALUES
(5, 5, 'ILA công bố học bổng tiếp sức nhân tài', 'Theo đó, từ nay đến tháng 1/2022, học viên đang theo học tại ILA, nếu giành được học bổng toàn phần vào các trường đại học hàng đầu của thế giới bao gồm: Harvard, Stanford, Massachusetts Institute of Technology (MIT), University of California at Berkeley, University of Cambridge sẽ có cơ hội nhận học bổng ILA Elite trị giá 500 triệu đồng/ người. Giải thưởng lớn này chỉ dành cho 5 học viên đầu tiên của ILA có hồ sơ hợp lệ theo yêu cầu chương trình. Đây được xem là hoạt động tiếp sức để các nhân tài có thêm động lực phấn đấu.', , 1),
(6, 6, 'ILA tiếp sức cho học viên Việt Nam đến đại học Mỹ', 'Theo đó, từ nay đến tháng 1-2022, học viên nếu giành được học bổng toàn phần vào các trường đại học hàng đầu của thế giới như: Harvard, Stanford, Massachusetts Institute of Technology (MIT), University of California at Berkeley, University of Cambridge sẽ có cơ hội nhận học bổng ILA Elite trị giá 500 triệu đồng/suất. Giải thưởng lớn này chỉ dành cho 5 học viên đầu tiên của ILA có hồ sơ hợp lệ theo yêu cầu chương trình.', , 1),
(7, 7, 'ILA CÔNG BỐ KHỞI ĐỘNG “HÀNH TRÌNH KIẾN TẠO THẾ HỆ VIỆT NAM ƯU VIỆT” NHÂN DỊP RA MẮT CUỐN SÁCH CỦA GIÁO SƯ JOHN VŨ Events News 16/03/2017    ', 'Phát biểu trong buổi họp báo, bà Trần Xuân Dzu, tổng giám đốc Trung tâm Anh ngữ ILA, cho biết: “Mong muốn lớn nhất của chúng tôi là nhìn thấy thế hệ trẻ Việt Nam tự tin và đường hoàng sánh vai các tài năng trẻ thế giới. Chúng tôi sẽ đào tạo thế hệ trẻ Việt Nam trở thành những thiếu niên ưu việt và trở thành những công dân ưu tú cho đất nước. Chính thức khởi động chương trình từ hôm nay, chúng tôi hi vọng chỉ trong 5 năm đến 10 năm nữa, Việt Nam có thể đón những gương mặt nổi bật, đóng góp vào sự phát triển của đất nước”.\r\n ', , 1),
(8, 8, '''Học điều mới, làm điều hay'' tại lễ hội ILA Beyond Christmas', 'Nhiều người sẽ đồng ý rằng, quá trình chuẩn bị, trang trí cho những ngày lễ lớn cũng chính là khoảng thời gian tận hưởng không khí lễ hội một cách trọn vẹn nhất.\r\n ', , 1);
INSERT INTO `ila_news_lang` (`id`, `news_id`, `title`, `short_desc`, `content`, `language_id`) VALUES
(9, 9, 'Quà Giáng sinh ý nghĩa từ ILA', 'Thầy Jonathan Bird - Giám đốc học vụ ILA chia sẻ: “Với sứ mệnh chuyển giao ngôn ngữ để thế hệ trẻ Việt Nam có thể tự tin sử dụng tiếng Anh và thành công trong cuộc sống, ILA luôn tạo mọi điều kiện thuận lợi cho các học viên tiếp cận dễ dàng hơn với môi trường học tiếng Anh chuẩn”. Chương trình đặc biệt nhân dịp lễ Giáng Sinh năm nay chính là một cam kết cho sứ mệnh đó.', , 1),
(10, 10, 'Học trò Việt viết thư cảm ơn giáo viên nước ngoài', 'Đây là những lời cảm ơn, chia sẻ chân thành của học sinh trường ILA dành cho thầy cô - là những giáo viên người nước ngoài trong ngày Nhà giáo Việt Nam.', , 1),
(11, 11, 'Kevin Siegmann - Thầy giáo người Mỹ và tâm huyết với học trò Việt', 'Giáo viên phải là người khơi nguồn cảm hứng”\r\nĐó là quan niệm của Kevin Siegmann về vai trò của người thầy trong việc giảng dạy tiếng Anh. Kevin cho rằng bước đầu tiên và vô cùng quan trọng để truyền đạt một ngôn ngữ mới là phải làm sao cho học viên cảm thấy hứng thú với mỗi buổi đến lớp.', , 1);

