INSERT INTO `ila_stories_lang` (`id`, `story_id`, `title`, `short_desc`, `long_desc`, `language_id`) VALUES
(1, 1, 'Nguyen Ngoc Phuong Uyen', 'I don’t know whether ILA students will read this letter or not. And, I don’t really know how much you truly love English or whether you are still lost in this language. However, I have dedicated this email for you. It’s simply sharing her story with honest words from her own experiences…to help you become more passionate about learning English.', , 1),
(2, 2, 'Phạm Thảo Uyên', 'SUMMER STUDY ABROAD PROGRAM - A ''TRIAL'' FOR LONG TERM STUDY ABROAD', , 1),
(3, 2, 'Phạm Thảo Uyên', 'DU HỌC HÈ “PHÉP THỬ” CỦA DU HỌC DÀI HẠN', , 2),
(4, 3, 'Nguyễn Việt Hùng Anh', 'Nguyen Viet Hung Anh is an energetic and cheerful ILA student who has recently achieved the highest score possible on the Cambridge PET exam. He learned about ILA from his English language teacher at school. For Hung Anh, class time at ILA is about “having fun, playing, and maximizing learning”.', , 1),
(5, 3, 'Nguyễn Việt Hùng Anh', 'Tuy chỉ 14 tuổi nhưng cậu bạn Lý Thành An đã có bảng thành tích ấn tượng khi liên tục vượt qua các kỳ thi Cambridge English với thành tích xuất sắc. Bí quyết lớn nhất cho những thành công này của An, không gì khác chính là sự tự tin.', , 2),
(6, 4, 'Lý Thành An', 'Ly Thanh An is only 14 years old, yet he has an extremely impressive scholastic achievement of passing all Cambridge English exams with excellent results. The key secret for his success is confidence.', , 1),
(7, 4, 'Lý Thành An', 'Tuy chỉ 14 tuổi nhưng cậu bạn Lý Thành An đã có bảng thành tích ấn tượng khi liên tục vượt qua các kỳ thi Cambridge English với thành tích xuất sắc. Bí quyết lớn nhất cho những thành công này của An, không gì khác chính là sự tự tin.', , 2);
INSERT INTO `ila_stories_lang` (`id`, `story_id`, `title`, `short_desc`, `long_desc`, `language_id`) VALUES
(8, 5, 'BÍ QUYẾT ĐẠT IELTS 7.0 CHỈ SAU 3 KHÓA HỌC TIẾNG ANH', 'How can you achieve a high score on this challenging test? Let’s meet Nguyen Vu Ha Yen Nhi who received 7.0 on IELTS after only three English courses at ILA and get to know her tips and strategies.', , 1),
(9, 5, 'BÍ QUYẾT ĐẠT IELTS 7.0 CHỈ SAU 3 KHÓA HỌC TIẾNG ANH', 'Làm thế nào để đạt được điểm cao trong kỳ thi cũng lắm cam go này? Cùng gặp gỡ bạn Nguyễn Vũ Hạ Yến Nhi, người đạt IELTS 7.0 chỉ sau 3 khóa học tiếng Anh tại ILA để nghe bạn “bật mí” bí quyết nằm trong tầm tay nhé!', , 2),
(10, 6, 'Nguyễn Quốc Hùng – Account Manager', 'Làm việc trong ngành quảng cáo, phải giao tiếp với các công ty nước ngoài nhiều nên rất cần trau dồi trình độ tiếng Anh, anh Nguyễn Quốc Hùng – Account manager một công ty quảng cáo lớn tại TP.HCM rất tích cực “nâng cấp” kiến thức Anh văn của mình. Tuy nhiên, Hùng tự nhận thấy, cách học của mình chưa chuyên nghiệp, nên chưa tiến bộ như ý muốn.', , 1),
(11, 7, 'GE- Nguyễn Phương Duy', 'Với Nguyễn Phương Duy, một trong số ít bạn trẻ đạt được cột mốc IELT 8.0 khi chỉ mới là học sinh lớp 10, thì tiếng Anh chỉ là “chuyện nhỏ” . ', , 1),
(12, 8, 'Trần Quang Nhân', 'Với những người làm kinh doanh, việc tạo được một tác phong chỉn chu, chuyên nghiệp trong công việc hay giao dịch với đối tác là điều rất quan trọng, nhất là với đối tác nước ngoài. Thế nhưng, làm sao bạn có thể gây ấn tượng tốt trong mắt đối tác và tự tin thương thảo những điều khoản có lợi cho mình, nếu không giỏi tiếng Anh?', , 1);
INSERT INTO `ila_stories_lang` (`id`, `story_id`, `title`, `short_desc`, `long_desc`, `language_id`) VALUES
(13, 9, 'Testimonial - Bé Bùi Mạnh Hoàng – Bậc Super Juniors - Mẹ: Vương Thúy Hà & Ba: Bùi Mạnh Hà', 'Gần như một quy tắc: Đội ngũ giáo viên là người nói tiếng Anh là tiếng mẹ đẻ + Cách tương tác với học sinh sống động, cụ thể thông qua các trò chơi…', , 1),
(14, 10, 'Testimonial - Bé Dương Vũ Quỳnh Chi – Bậc Học Super Juniors - Mẹ: Vũ Nguyệt Anh (Tốt Nghiệp Đại Học Sư Phạm Ngoại Ngữ – Hiện Làm Cho Công Ty Nước Ngoài)', 'Đó là quan điểm của mẹ bé Quỳnh Chi – bằng giọng nhỏ nhẹ của người gốc Huế, chị lý giải: “Khi dạy ngoại ngữ cho trẻ em tưởng dễ mà khó. Bởi não trẻ như tờ giấy trắng, chưa viết gì nếu viết sai, hằn lên sẽ rất khó sửa.', , 1),
(15, 11, 'Testimonial - Phụ Huynh Của Bé Trương Nguyễn Quỳnh Trâm – Bậc Học Super Junior - Ba: Bác Sĩ BV Nhi Đồng 2 - Trương Nguyễn Uy Linh & Mẹ: Phạm Thị Thanh Trang', '“Kích thích sự khám phá của trẻ, việc “tiếp nhận” Anh ngữ sẽ trở nên nhẹ nhàng và hiệu quả!”', , 1),
(16, 12, 'Testimonial - Bé Nguyễn Trắc Nhật Linh - Bậc Học Supper Juniors - Ba Nguyễn Trác Bá - Mẹ Tô Thanh Thủy', 'Ngỡ ngàng và hãnh diện khi thấy con giao tiếp Tiếng Anh tại sân bay Úc rất  tự tin, thoải mái…”\r\nNgay từ  khoảng 3 tuổi, bé  Nhật Linh đã thể hiện  năng khiếu về ngôn ngữ. Bé có “thẩm âm” khá tốt.  Từ bé xíu, Nhật Linh đã tỏ ra thích khi nghe nhạc và xem những chương trình  hoạt hình bằng tiếng Anh.', , 1),
(17, 13, 'Testimonial - Bé Nguyễn Xuân Minh Thy (9 Tuổi) – Bậc Học Supper Juniors & Bé Xuân Bắc (7 Tuổi) - Mẹ: Mrs Thủy', '“Kỹ năng nghe nói tốt, sẽ giúp trẻ tiếp cận ngoại ngữ hiệu quả!”\r\nTrên quan điểm này, gia đình đã quyết định tìm cho được một môi trường giảng dạy Anh ngữ tiệm cận nhất với môi trường sử dụng tiếng Anh như bản ngữ – Mẹ Thủy đã mở đầu câu chuyện về việc đầu tư cho hai con của mình theo học tại ILA.', , 1),
(18, 14, 'Testimonials - Ba Bé Nguyễn Đình Minh Việt (6 Tuổi) – Theo Học Tại ILA Từ Lúc 4,5 Tuổi', '“Phương pháp học mà chơi ILA áp dụng cho độ tuổi thiếu nhi là cực kỳ hợp lý”\r\nVới bản tính nhút nhát, bé Minh Việt rất ít dám thể hiện bản thân. Thế nhưng, khi được ba mẹ cho tham gia học thử tại ILA thì bé đã tỏ ra bị môi trường này thu hút ngay từ đầu. Và thế là theo học luôn tại đây gần một năm rưỡi qua  - Ba Minh Việt chia sẻ.', , 1),
(19, 15, 'Testimonials - Me Thu Giang – Có 2 Con Minh Anh (5 Tuổi) Và Sĩ Khải (17 Tuổi)', '“Học Anh văn tại ILA giúp tiếp cận và hòa nhập nhanh, khi du học nước ngoài!”\r\n \r\nĐó là chia sẻ của mẹ Thu Giang,  sau khi đúc kết kinh nghiệm của cậu con trai Sĩ Khải theo học tại ILA từ hơn 10 năm trước (lúc Khải 6 tuổi), hiện Khải đang học tại một trường phổ thông ở Singapore.', , 1),
(20, 16, 'Testimonials - Ba Mẹ Của Hai Bé Trần Anh Thu (11 Tuổi ) Và Trần Quang Khải (6 Tuổi)', '“So với xu hướng chung, hai con tôi theo học Anh văn có hơi nuộn!”\r\nHọc ngoại ngữ - nhất là tiếng Anh, trong những năm gần đây đã và đang trở thành xu hướng. Với thế hệ  của hai bé nhà tôi, việc trang bị thêm ngoại ngữ Tiếng Anh  là điều gần như “bắt buộc”.', , 1);
INSERT INTO `ila_stories_lang` (`id`, `story_id`, `title`, `short_desc`, `long_desc`, `language_id`) VALUES
(21, 17, 'Testimonials - Ba Mẹ Của Hai Bé Nguyễn Phạm Vương Khang (6 Tuổi) Và Nguyễn Phạm Gia Vương (3 Tuổi)', '“Nếu được tiếp cận ngoại ngữ sớm, khả năng tiếp thu của bé sẽ tốt hơn!”\r\nĐó là điều mà gia đình đã “kiểm chứng” từ hai anh em Vương Khang và Gia Vương – Ba và mẹ hai bé chia sẻ. Với bé Vương Khang, độ tuổi bé bắt đầu tiếp xúc với việc học tiếng Anh là lúc bé 4 tuổi, đến nay bé theo học ILA được 2 năm.', , 1),
(22, 18, 'Testimonials - Phụ Huynh Bé Vũ Khánh Linh - 5 Tuổi', 'Băn khoăn về độ tuổi hợp lý cho con bắt đầu tiếp xúc với Ngoại ngữ!\r\nTheo tìm hiểu qua sách báo, thông tin trên mạng (kể cả trong và ngoài nước) gia đình tôi vẫn biết cho bé tiếp xúc với ngoại ngữ sớm sẽ có nhiều lợi ích. Thế nhưng, cụ thể sớm là ở độ tuổi nào thì thật sự  gia đình cũng còn khá băn khoăn – phụ huynh bé Khánh Linh cho biết ý kiến.', , 1),
(23, 19, 'Testimonial - Anh Nguyễn Đức Thắng – Phụ Huynh Bé Nguyễn Đức Khoa', '“Giải đáp ngay thắc mắc, sẽ giúp bé “hấp thụ” kiến thức nhanh và nhớ lâu hơn!”\r\nĐó là quan điểm của anh Nguyễn Đức Thắng – phụ huynh của bé Khoa (14 tuổi – bậc học Smart Teens tại ILA).', , 1),
(24, 20, 'Testimonial - Bé Bùi Thúy Vy – 15 Tuổi –Bậc Học Smart Teens - Ba: Bùi Thanh Tân - Mẹ: Đặng Thị Thanh Thúy', '“Xuất phát điểm là thích tiếng Nhật, nhưng khi đã học thì tiếng Anh cũng rất “đỉnh!”', , 1),
(25, 21, 'Testimonial - Bé Nguyễn Gia Hân (13 Tuổi) - Bậc Học Smart Teens - Ba: Nguyễn Hữu Tâm – Mẹ: Lưu Thị Nhất Hương', '“Tôi đánh giá cao hiệu quả khóa du học học hè của ILA”', , 1),
(26, 22, 'Testimonial - Bé Nguyễn Lợi Thanh Chi (12 Tuổi) - Bậc Học Smart Teens - Ba: Nguyễn Vĩnh Lợi - Mẹ: Nguyễn Lê Đinh Thanh', 'Môi trường học thân thiện đã giúp con tôi yêu thích Tiếng Anh!”\r\n', , 1),
(27, 23, 'Testimonial - Bé Nguyễn Bảo Anh (12 Tuổi) – Bậc Học Smart Teens - Mẹ: Thanh Đào', '“Hãy “kích thích” trẻ phát triển Anh ngữ từ những đam mê riêng biệt”', , 1),
(28, 24, 'Testimonial - Anh Nguyễn Đức Thắng – Phụ Huynh Bé Nguyễn Đức Khoa', '“Giải đáp ngay thắc mắc, sẽ giúp bé “hấp thụ” kiến thức nhanh và nhớ lâu hơn!”\r\n', , 1),
